import React from 'react';
import ReactDOM from 'react-dom';


class TodoApp extends React.Component {
  constructor(props) {
    super(props);
    this.state = { items: [],text:'' };
    this.handleTableChange = this.handleTableChange.bind(this);
    this.handleChickenChange = this.handleChickenChange.bind(this);
    this.handlePepsiChange = this.handlePepsiChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  render() {
    return (
      <div>
        <h3> First & Friend Fried Chicken</h3>
        <TodoList items={this.state.items} />
        What would you like to order?

        <form onSubmit={this.handleSubmit}>
        <label>
          Table:
        </label>
        <input

          onChange={this.handleTableChange}
          name="Table"
        /> <br/>

          <label >
            Chicken:
          </label>
          <input
            onChange={this.handleChickenChange}
            name="chicken"
          /><br/>
          <label >
            Pepsi:
          </label>
          <input
            onChange={this.handlePepsiChange}
            name="pepsi"
          /><br/>

          <button>
            Add new order
          </button>
        </form>
      </div>
    );
  }
  handleTableChange(e) {
    this.setState({ text1: e.target.value });
  }

  handleChickenChange(e) {
    this.setState({ text2: e.target.value });
  }

  handlePepsiChange(e) {
    this.setState({ text3: e.target.value });
  }

  handleSubmit(e) {
    e.preventDefault();
    if (this.state.text1.length === 0) {
      return;
    }
    const newItem = {
      text: 'Table: '+  this.state.text1+ '  Chicken :'+ this.state.text2 +
      '  Pepsi :'+ this.state.text3 + '  Total :'+  (this.state.text2*15 + this.state.text3*7),
      id: Date.now()
    };
    this.setState(state => ({
      items: state.items.concat(newItem),
      text: ''
    }));
  }
}

class TodoList extends React.Component {
  render() {
    return (
      <ul>
        {this.props.items.map(item => (
          <li key={item.id}>{item.text}</li>
        ))}
      </ul>
    );
  }
}

ReactDOM.render(
  <TodoApp />,
  document.getElementById('todos-example')
);
