import React from 'react';
import ReactDOM from 'react-dom';


class TodoApp extends React.Component {
  constructor(props) {
    super(props);
    this.state = { items: [], text1: '', text2: '' };
    this.handleChickenChange = this.handleChickenChange.bind(this);
    this.handlePepsiChange = this.handlePepsiChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  render() {
    return (
      <div>
        <h3>TODO</h3>
        What would you like to order
        <TodoList items={this.state.items} />
        <form onSubmit={this.handleSubmit}>
          <label htmlFor="new-chicken">
            Chicken
          </label>
          <input
            id="new-todo"
            onChange={this.handleChickenChange}
            name="chicken"
          />
          <label htmlFor="new-pepsi">
            Pepsi
          </label>
          <input
            id="new-pepsi"
            onChange={this.handlePepsiChange}
            name="pepsi"
          />

          <button>
            Add #{this.state.items.length + 1}
          </button>
        </form>
      </div>
    );
  }

  handleChickenChange(e) {
    this.setState({ text1: e.target.value });
  }


  handlePepsiChange(e) {
    this.setState({ text1: e.target.value });
  }

  handleSubmit(e) {
    e.preventDefault();
    if (this.state.text1.length === 0) {
      return;
    }
    const newItem = {
      text: this.state.text1 + this.state.text1,
      id: Date.now()
    };
    this.setState(state => ({
      items: state.items.concat(newItem),
      text: ''
    }));
  }
}

class TodoList extends React.Component {
  render() {
    return (
      <ul>
        {this.props.items.map(item => (
          <li key={item.id}>{item.text}</li>
        ))}
      </ul>
    );
  }
}

ReactDOM.render(
  <TodoApp />,
  document.getElementById('todos-example')
);
